mapper.select:
SELECT E.EMPLOYEE_ID, E.FIRST_NAME, E.LAST_NAME, E.EMAIL, E.HIRE_DATE, D.DEPARTMENT_NAME, C.JOB_TITLE FROM EMPLOYEES E INNER JOIN DEPARTMENTS D ON E.DEPARTMENT_ID = D.DEPARTMENT_ID INNER JOIN JOBS C ON E.JOB_ID = C.JOB_ID
DEPARTMENT_ID = #{department_id}
AND JOB_ID = #{job_id}
AND upper(FIRST_NAME) LIKE '%' || upper(#{first_name}) || '%'

mapper.update:
UPDATE 
EMPLOYEES 
SET 
EMAIL = #{email} 
WHERE EMPLOYEE_ID = #{employee_id}
